import React, { useState } from 'react';

const App = () => {
    //hooks create 
    const [inputList, setinputList] = useState("");
    const [items, setItems] = useState([]);           //  [] -> empty array (currently)


    const taskEvent = (event) => {
        setinputList(event.target.value);
        // setinputList -> inputList -> li

    };

    const addTask = () => {
        setItems((olditems) => {
            return[...olditems,]       //it will return the old list of task that is stored in the array using "spread operator"

        })

    };




    return (
        <>
            <div className='main_div'>
                <div className="center_div">
                    <br />
                    <h1 className="heading">To Do List</h1>
                    <br />

                    <input className="entertext" type="text" placeholder="Add task" onChange={taskEvent}>
                        {/* jab bhi kuch type krenge to "onChange" call hoga  */}
                    </input>
                    <button onClick={addTask}> + </button>

                    <ul>
                        {/* <li> {inputList} </li> */}

                        {items.map((itemval) => {
                            return <li>{itemval}</li>

                        })}
                    </ul>
                </div>
            </div>
        </>

    )
};



export default App;
